

/**
 * Represents an image with the following properties:
 * @typedef {object} Image
 * @property {string} url - The URL of the image.
 * @property {number} height - The height of the image.
 * @property {number} width - The width of the image.
 */
export type Image = {
  url: string;
  height: number;
  width: number;
}